import layout from "./components/layout.vue";
import Gform from "./components/form.vue";
export default {
  name: "login",
  components: {
    layout: layout,
    Gform: Gform
  },
  methods: {
    doSubmit: function doSubmit(data) {
      var _this = this;

      this.$axios.post("/cs/login", data) //请求接口/cs/regist，
      .then(function (resp) {
        //当服务器返回结果后处理
        var data = resp.data;

        if (data.success) {
          localStorage.setItem("user", JSON.stringify(data.result));

          _this.$router.push("/index");
        }
      }).catch(function (err) {
        //当请求失败，处理
        console.log(err);
      });
    }
  }
};
